// PropTypes
import PropTypes from 'prop-types'

// Component
import Tab from '../../components/Tab'

// Style
import './style.scss'


function Header ({activeTab}) {

  let displayIcon = "fa-solid fa-moon"

  function selectDisplay (e) {
    e.preventDefault()
    (displayIcon !== "fa-solid fa-sun") ? "fa-solid fa-sun" : "fa-solid fa-moon"
  }

  return (
    <header className='header'>
      <div className='header_container'>
        <button className='header_button' onClick={(e) => selectDisplay(e)}><i className={displayIcon}></i></button>
        <nav className='header_nav'>
          <Tab isActive={activeTab==="profile"} path="/" text="PROFIL"/>
          <Tab isActive={activeTab==="projects"} path="/projects" text="PROJETS"/>
          <Tab isActive={activeTab==="contact"} path="/contact" text="CONTACT"/>
        </nav>
      </div>
    </header>
  )
}

Header.propTypes = {
  activeTab: PropTypes.string
}

export default Header
