// React
import { useState } from 'react'

// PropTypes
import PropTypes from 'prop-types'

// Component
import Tab from '../../components/Tab'

// Style
import './style.scss'

// Redux
import { useDispatch, useSelector } from 'react-redux'
import { displaySelect, displayUnselect } from '../../redux/displayActions'
import { select, unselect } from '../../redux/displaySlice'


function Header ({activeTab}) {

  const { nightMode } = useSelector((state) => state.display)
  const [displayIconValue, setdisplayIconValue] = useState("fa-solid fa-moon")
  const dispatch = useDispatch()

  function selectDisplay () {
    if (displayIconValue !== "fa-solid fa-sun") {
      setdisplayIconValue("fa-solid fa-sun")
      dispatch(unselect())
    }  
    else {
      setdisplayIconValue("fa-solid fa-moon")
      dispatch(select())
    } 

  }

  return (
    <header className='header'>
      <div className='header_container'>
        <button className='header_button' onClick={() => selectDisplay()}><i className={displayIconValue}></i></button>
        <nav className='header_nav'>
          <Tab isActive={activeTab==="profile"} path="/" text="PROFIL"/>
          <Tab isActive={activeTab==="projects"} path="/projects" text="PROJETS"/>
          <Tab isActive={activeTab==="contact"} path="/contact" text="CONTACT"/>
        </nav>
      </div>
    </header>
  )
}

Header.propTypes = {
  activeTab: PropTypes.string
}

export default Header
