// Component
import Tab from '../../components/Tab'

// Style
import './style.scss'


function Header () {

  function goTo(e, path) {
    e.preventDefault()
    navigate(path)
  }

  return (
    <header>
    <nav className="main-nav">
      <div className="logo">Amandine Cartier-Michaud</div>>
      <div className="tab-container">
          <Tab action={(e) => goTo(e, "/")} iconClass="icon fa fa-user-circle" text="Profile"/>
          <Tab action={(e) => goTo(e, "/projects")} iconClass="icon fa fa-sign-out" text="Projets"/>
          <Tab action={(e) => goTo(e, "/contact")} iconClass="icon fa fa-sign-out" text="Contact"/>
      </div>
    </nav>
    </header>
  )
}

export default Header
